function [outs]=Skripta_napoved_struktur_5_7_2016(seq,width) %width je vektor 5% poberem iz nnx predelave za p64load('NN_structural_properties.mat')load('NNN_structural_properties.mat')x = length(seq);ntseq = nt2int(seq);jnn = [1 33 34 37];out = cell(6,1);for i = 1:4 % specific properties  j = jnn(i);    for k = 1:x-1  % NN          out{i}(k) = NNsp{j+2, 6+(4*(ntseq(k)-1)+ntseq(k+1))};  end          for k=1:x-(width(i)-1)-1 % sum over width      if j == 33 %persistence              outs{i}(k) = width(i)/sum(1./out{i}(k:k+width(i)-1));            else              outs{i}(k) = sum(out{i}(k:k+width(i)-1));              end   endend for i = 1 % dnaze      for k=1:x-2 % NNN        l=1;      while (strcmp(NNNsp{l,1}(1:3),seq(k:k+2)) || strcmp(NNNsp{l,1}(5:7),seq(k:k+2))) == 0; %loop breaks when NNN is found            l=l+1;      end      out{5}(k) = NNNsp{l,2};  end      for k=1:x-(width(5)-2)-2        outs{5}(k) = sum(out{5}(k:k+width(5)-2));  end        end    tmpt = weka_run_klas_10(1,seq,x);outs{6} = tmpt';%dG%persistence%helical%deformability%DNAze%TIDDend